VALUES ('84.051', 'Career and Technical Education -- National Programs', '', 'Office of Career, Technical, and Adult Education, EDUCATION, DEPARTMENT OF', 'To provide support, directly or through grants, contracts, or cooperative agreements, for  research, development, demonstration, dissemination, evaluation, assessment, capacity-building, and technical assistance activities aimed at improving the quality and effectiveness of career and technical education (CTE) programs authorized under the Perkins Act V.', 'PROJECT GRANTS', 'CTE sole State agencies; local educational agencies; CTE providers, and CTE students.', 'https://www2.ed.gov/programs/venp/index.html', '{"flag":"contact","list":[]}', 0, 0, 1, 1, 0, '(Project Grants) FY 23$3,875,794.00; FY 24 est $33,757,415.00; FY 25 est $64,421,000.00; FY 22$7,421,000.00; FY 21$7,421,000.00; FY 20$7,421,000.00; FY 19$7,421,000.00; FY 18$7,421,000.00; FY 17$7,421,000.00; FY 16$7,421,000.00; -', 'Varies by program competition.', '{"list":[{"fiscalYear":2016,"description":"No Current Data Available."},{"fiscalYear":2017,"description":"No Current Data Available."},{"fiscalYear":2018,"description":"No Current Data Available."},{"fiscalYear":2019,"description":"No Current Data Available."},{"fiscalYear":2024,"description":"No Current Data Available."}],"isApplicable":true}');

