VALUES ('84.411', 'Education Innovation and Research (formerly Investing in Innovation (i3) Fund)', '', 'OFFICE OF ELEMENTARY AND SECONDARY EDUCATION, EDUCATION, DEPARTMENT OF', 'To support the creation, development, implementation, replication, and scaling up of evidence-based, field-initiated innovations designed to improve student achievement and attainment for high-need students through a tiered-evidence framework that supports larger awards for projects with the strongest evidence base and supports promising earlier-stage projects that undergo rigorous evaluation.', 'PROJECT GRANTS', 'High-need students in LEAs will benefit.', 'https://www.ed.gov/grants-and-programs/grants-special-populations/economically-disadvantaged-students/education-innovation-and-research', '{"flag":"contact","list":[]}', 0, 0, 1, 0, 0, '(Project Grants) FY 23$161,939,034.00; FY 24 est $277,337,948.00; FY 25 est $251,000,000.00; FY 22$254,453,995.00; FY 21$185,987,143.00; FY 20$189,304,763.00; FY 19$130,000,000.00; FY 18$120,000,000.00; FY 17$100,000,000.00; FY 16$120,000,000.00; -', 'Est. Range:  $3,000,000-$15,000,000.', '{"list":[{"fiscalYear":2016,"description":"No Current Data Available."},{"fiscalYear":2017,"description":"No Current Data Available."},{"fiscalYear":2018,"description":"No Current Data Available."},{"fiscalYear":2019,"description":"No Current Data Available."},{"fiscalYear":2020,"description":"No Current Data Available."}],"isApplicable":false}');

