PRAGMA defer_foreign_keys=TRUE;
CREATE TABLE metric_definitions (
  metric_key TEXT PRIMARY KEY,
  display_name TEXT NOT NULL,
  category TEXT NOT NULL, -- 'population', 'housing', 'income', 'education'
  description TEXT,
  census_table TEXT, -- Source Census table (e.g., 'DP05', 'S1901')
  census_variable TEXT, -- Source variable ID
  unit TEXT, -- 'count', 'currency', 'percent', 'year'
  sort_order INTEGER
);
